mapper.select:
b.BK_ID,
b.BK_TITLE,
b.BK_PROCESS_P,
b.BK_REPORT_P,
b.BK_TIME,
b.BK_SUBSYS,
b.BK_REP_STATUS,
b.BK_PANELAUDITOR
from
 b
WHERE 1=1
and

	b.BK_TITLE LIKE CONCAT(CONCAT('%', #{BK_TITLE}),'%')


	and b.BK_TIME >= #{startTime}


	and b.BK_TIME <= #{endTime}



	and b.BK_SUBSYS=#{BK_SUBSYS}


	and b.BK_PROCESS_P=#{BK_PROCESS_P}


	and b.BK_REPORT_P=#{BK_REPORT_P}


	and b.BK_REP_STATUS=#{BK_REP_STATUS}

select:
select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.END_TIME, u.YEARS, u.STATUS, u.EMAIL, u.PHONE, u.NUMBER, r.ROLE_ID, r.ROLE_NAME from u, r where u.ROLE_ID = r.ROLE_ID and r.PARENT_ID = '2'
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and u.ROLE_ID=#{pd.ROLE_ID}
and u.END_TIME >= #{pd.lastLoginStart}
and u.END_TIME <= #{pd.lastLoginEnd}
and u.STATUS=#{pd.STATUS}
order by u.NAME

select 

from 

where 
	EMAIL = #{EMAIL}

	and USERNAME != #{USERNAME}

select 

from 

where 
	NUMBER = #{NUMBER}

	and USERNAME != #{USERNAME}

update:
update set USERNAME = #{USERNAME}, NAME = #{NAME}, ROLE_ID = #{ROLE_ID}, BZ = #{BZ}, PHONE = #{PHONE}, SFID = #{SFID}, START_TIME = #{START_TIME}, END_TIME = #{END_TIME}, YEARS = #{YEARS}, STATUS = #{STATUS}, EMAIL = #{EMAIL}, NUMBER = #{NUMBER}
,PASSWORD = #{PASSWORD}
where USER_ID = #{USER_ID}

select:
select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.END_TIME, u.YEARS, u.STATUS, u.EMAIL, u.PHONE, u.SFID, u.NUMBER, r.ROLE_ID, r.ROLE_NAME from u, r where u.ROLE_ID = r.ROLE_ID and r.PARENT_ID = '2'
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{keywords}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{keywords}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
and u.ROLE_ID=#{ROLE_ID}
and u.END_TIME >= #{lastLoginStart}
and u.END_TIME <= #{lastLoginEnd}
